import Surface from"../Surface.js";let gaussParams={a:1,b:1,c:0};class Gaussian extends Surface{constructor(a){super(a)}setParamData(){this.params={a:1,b:1,c:0},this.paramData={a:{min:0,max:5,step:.01,name:"Amplitude"},b:{min:0,max:5,step:.01,name:"Concentration"},c:{min:-3,max:3,step:.01,name:"Position"}}}setFunctionData(){super.setFunctionData();let a=this.params.a,t=this.params.b,s=this.params.c;this.F=function(e,n){return a*Math.exp(-t*((e-s)*(e-s)+n*n))},this.name="Gaussian",this.Ftxt=`f(u,v)=${a}*exp(-${t}*((u-${s})^2 + v^2))`}}export default Gaussian;