import Surface from"../Surface.js";class SinxSiny extends Surface{constructor(a){super(a)}setParamData(){this.params={a:.4,b:4},this.paramData={a:{min:0,max:2,step:.01,name:"Amplitude"},b:{min:0,max:6,step:.01,name:"Frequency"}}}setFunctionData(){super.setFunctionData();const a=this.params.a,t=3.14159*this.params.b/this.domain.u.max,s=t;this.F=function(i,n){return a*(Math.sin(t*i)+Math.sin(s*n))},this.name="Sinusoid",this.Ftxt=`f(u,v)=${a}*(sin(${t}*u)+sin(${s}*v))`}}export default SinxSiny;