import ThreeBody from"../ThreeBody.js";import{pA,pB,pC}from"./params.js";const example=new ThreeBody(pA,pB,pC);import World from"../../../../code/World/World.js";import{createEnvironment}from"../../../../code/World/template/environment.js";import{lights}from"../../../../code/World/template/lights.js";let globalSettings={name:"World",stats:!1,environment:{color:2698038,cube:!0},camera:{animate:!1,fov:55,pos:{x:.5,y:1,z:5},look:{x:0,y:0,z:0},posAnimate:e=>({x:15*Math.sin(Math.sin(e/5)),y:7,z:15*Math.cos(Math.sin(e/5))}),lookAnimate:e=>({x:0,y:-5,z:0})},controls:{minDistance:0,maxDistance:100}};function main(e){const o=new World(e),t=createEnvironment(e.environment,o.pmrem);o.setEnvironment(t);let n={example:example};o.addObjects(n),o.addObjects(lights),o.start()}main(globalSettings);